import React, { Component, useState } from "react";
import { NavLink } from "react-router-dom";

import {
  UncontrolledDropdown,
  DropdownItem,
  DropdownToggle,
  DropdownMenu,
  Input
} from "reactstrap";

import {
    menuHiddenBreakpoint,
    searchPath,
    localeOptions,
    isDarkSwitchActive
  } from "../../constants/defaultValues";

import { MobileMenuIcon, MenuIcon } from "../forms/svg/index";
import TopnavDarkSwitch from "./Topnav.DarkSwitch";
import TopnavEasyAccess from "./Topnav.EasyAccess";
import TopnavNotifications from "./Topnav.Notifications";


function Navbar() {
    const [sidebar,setSidebar]=useState(false);
    const [isInFullScreen,setisInFullScreen]=useState(false)
   // const { containerClassnames, menuClickCount, locale } = this.props;
const menuClickCount=0;
const containerClassnames='menu-sub-hidden';


 

const setContainerClassnames = (clickIndex, strCurrentClasses,selectedMenuHasSubItems) => {
    const currentClasses = strCurrentClasses ? strCurrentClasses.split(' ').filter(x => x !== '') : '';
    let nextClasses = '';
    if (!selectedMenuHasSubItems) {
        if (currentClasses.includes("menu-default") && (clickIndex % 4 === 0 || clickIndex % 4 === 3)) {
          clickIndex=1;
        }
        if (currentClasses.includes("menu-sub-hidden") && (clickIndex % 4 === 2)) {
          clickIndex=0;
        }
        if (currentClasses.includes("menu-hidden") && (clickIndex % 4 === 2 || clickIndex % 4 === 3 )) {
            clickIndex=0;
          }

        }
    }



    const menuButtonClick = (e, menuClickCount, containerClassnames) => {
        e.preventDefault();
    
        setTimeout(() => {
          var event = document.createEvent("HTMLEvents");
          event.initEvent("resize", false, false);
          window.dispatchEvent(event);
        }, 350);
       setContainerClassnames(
          ++menuClickCount,
          containerClassnames,
         // this.props.selectedMenuHasSubItems
         containerClassnames
        );
      };  
      
     

const showSidebar= ()=>{setSidebar(!sidebar)}
return (
    <nav className="navbar fixed-top">
      <div className="d-flex align-items-center navbar-left">
        <NavLink
          to="#"
          className="menu-button d-none d-md-block"
          onClick={e =>    menuButtonClick(e, menuClickCount, containerClassnames)}
        >
          <MenuIcon />
        </NavLink>
        <NavLink
          to="#"
          className="menu-button-mobile d-xs-block d-sm-block d-md-none"
       //   onClick={e => this.mobileMenuButtonClick(e, containerClassnames)}
        >
          <MobileMenuIcon />
        </NavLink>

        <div className="search" data-search-path="/app/pages/search">
          <Input
            name="searchKeyword"
            id="searchKeyword"
            placeholder="search"
           // value={this.state.searchKeyword}
            onChange={e => this.handleSearchInputChange(e)}
            onKeyPress={e => this.handleSearchInputKeyPress(e)}
          />
          <span
            className="search-icon"
            onClick={e => this.handleSearchIconClick(e)}
          >
            <i className="simple-icon-magnifier" />
          </span>
        </div>

        <div className="d-inline-block">
          <UncontrolledDropdown className="ml-2">
            <DropdownToggle
              caret
              color="light"
              size="sm"
              className="language-button">
              <span className="name">{
              //locale.toUpperCase()
            "hedi"
            }</span>
            </DropdownToggle>
            <DropdownMenu className="mt-3" right>
              {localeOptions.map(l => {
                return (
                  <DropdownItem
                    onClick={() => this.handleChangeLocale(l.id, l.direction)}
                    key={l.id}
                  >
                    {l.name}
                  </DropdownItem>
                );
              })}
            </DropdownMenu>
          </UncontrolledDropdown>
        </div>
        <div className="position-relative d-none d-none d-lg-inline-block">
          <a
            className="btn btn-outline-primary btn-sm ml-2"
            target="_top"
            href="https://themeforest.net/cart/configure_before_adding/22544383?license=regular&ref=ColoredStrategies&size=source"
          >
           buy
          </a>
        </div>
      </div>
      <a className="navbar-logo" href="/">
        <span className="logo d-none d-xs-block" />
        <span className="logo-mobile d-block d-xs-none" />
      </a>

      <div className="navbar-right">
        {isDarkSwitchActive && <TopnavDarkSwitch/>}
        <div className="header-icons d-inline-block align-middle">
          <TopnavEasyAccess />
          <TopnavNotifications />
          <button
            className="header-icon btn btn-empty d-none d-sm-inline-block"
            type="button"
            id="fullScreenButton"
           // onClick={this.toggleFullScreen}
          >
            {isInFullScreen ? (
              <i className="simple-icon-size-actual d-block" />
            ) : (
              <i className="simple-icon-size-fullscreen d-block" />
            )}
          </button>
        </div>
        <div className="user d-inline-block">
          <UncontrolledDropdown className="dropdown-menu-right">
            <DropdownToggle className="p-0" color="empty">
              <span className="name mr-1">Sarah Kortney</span>
              <span>
                <img alt="Profile" src="/assets/img/profile-pic-l.jpg" />
              </span>
            </DropdownToggle>
            <DropdownMenu className="mt-3" right>
              <DropdownItem>Account</DropdownItem>
              <DropdownItem>Features</DropdownItem>
              <DropdownItem>History</DropdownItem>
              <DropdownItem>Support</DropdownItem>
              <DropdownItem divider />
              <DropdownItem onClick={() => this.handleLogout()}>
                Sign out
              </DropdownItem>
            </DropdownMenu>
          </UncontrolledDropdown>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
