import React from "react";
import { Card, CardBody, CardTitle } from "reactstrap";
import PerfectScrollbar from "react-perfect-scrollbar";
import ReactTable from "react-table";
import classnames from "classnames";
import DataTablePagination from "./DataTablePagination";
//import IntlMessages from "../../helpers/IntlMessages";
//import DataTablePagination from "../../components/DatatablePagination";




const CustomTbodyComponent = props => (
    <div {...props} className={classnames("rt-tbody", props.className || [])}>
      <PerfectScrollbar options={{ suppressScrollX: true }}>
        {props.children}
      </PerfectScrollbar>
    </div>
  );
  
  const dataTableColumns = [
    {
      Header: "Name",
      accessor: "title",
      Cell: props => <p className="list-item-heading">{props.value}</p>
    },
    {
      Header: "Sales",
      accessor: "sales",
      Cell: props => <p className="text-muted">{props.value}</p>
    },
    {
      Header: "Stock",
      accessor: "stock",
      Cell: props => <p className="text-muted">{props.value}</p>
    },
    {
      Header: "Category",
      accessor: "category",
      Cell: props => <p className="text-muted">{props.value}</p>
    }
  ];
  const datTest=[{nom : 'hedi',
age : '55'
}]


const columns = [{
    Header: 'Name',
    accessor: 'name' // String-based value accessors!
  }, {
    Header: 'Age',
    accessor: 'age',
    Cell: props => <span className='number'>{props.value}</span> // Custom cell components!
  }, {
    id: 'friendName', // Required because our accessor is not a string
    Header: 'Friend Name',
    accessor: d => d.friend.name // Custom value accessors!
  }, {
    Header: props => <span>Friend Age</span>, // Custom header components!
    accessor: 'friend.age'
  }]

  
  export const ReactTableWithPaginationCard = props => {
    return (
      <Card className="mb-4">
        <CardBody>
          <CardTitle>
          pagination
          </CardTitle>
          <ReactTable
            data={dataTableColumns}
            columns={dataTableColumns}
            defaultPageSize={5}
            showPageJump={false}
            showPageSizeOptions={false}
            PaginationComponent={DataTablePagination}
            className={"react-table-fixed-height"}
          />
        </CardBody>
      </Card>
    );
  };
  export const ReactTableWithScrollableCard = props => {
    return (
      <Card className="mb-4">
        <CardBody>
          <CardTitle>
           scrollable
          </CardTitle>
          <ReactTable
            data={props.data}
            TbodyComponent={CustomTbodyComponent}
            columns={dataTableColumns}
            defaultPageSize={20}
            showPageJump={false}
            showPageSizeOptions={false}
            showPagination={false}
            className={"react-table-fixed-height"}
          />
        </CardBody>
      </Card>
    );
  };
   const ReactTableAdvancedCard = ({className="mb-4", icon, title, value,data,dataTableColumns1 })  => {
    return (
      <Card className="mb-4">
        <CardBody>
          <CardTitle>
           advanced
          </CardTitle>
          <ReactTable

data={datTest}
//options={"10","20"}
            columns={columns}
            defaultPageSize={5}
            filterable={true}
            showPageJump={true}
            PaginationComponent={DataTablePagination}
            showPageSizeOptions={true}
          />
        </CardBody>
      </Card>
    );
  };
  
  export default ReactTableAdvancedCard;
